import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: true,
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth, isAuthenticated } from "./replitAuth";
import { insertChannelSchema, insertVideoSchema, insertCategorySchema } from "@shared/schema";
import multer from "multer";
import path from "path";

const upload = multer({ dest: "uploads/" });

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth middleware
  await setupAuth(app);

  // Auth routes
  app.get('/api/auth/user', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);
      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Categories routes
  app.get('/api/categories', async (req, res) => {
    try {
      const categories = await storage.getCategories();
      res.json(categories);
    } catch (error) {
      console.error("Error fetching categories:", error);
      res.status(500).json({ message: "Failed to fetch categories" });
    }
  });

  app.post('/api/categories', isAuthenticated, async (req, res) => {
    try {
      const categoryData = insertCategorySchema.parse(req.body);
      const category = await storage.createCategory(categoryData);
      res.status(201).json(category);
    } catch (error) {
      console.error("Error creating category:", error);
      res.status(500).json({ message: "Failed to create category" });
    }
  });

  // Channels routes
  app.get('/api/channels', async (req, res) => {
    try {
      const userId = req.query.owner as string;
      if (userId) {
        const channels = await storage.getChannelsByOwner(userId);
        res.json(channels);
      } else {
        res.status(400).json({ message: "Owner ID required" });
      }
    } catch (error) {
      console.error("Error fetching channels:", error);
      res.status(500).json({ message: "Failed to fetch channels" });
    }
  });

  // Featured channels endpoint
  app.get('/api/channels/featured', async (req, res) => {
    try {
      // For MVP, return empty array - can be enhanced later with actual featured logic
      res.json([]);
    } catch (error) {
      console.error("Error fetching featured channels:", error);
      res.status(500).json({ message: "Failed to fetch featured channels" });
    }
  });

  app.get('/api/channels/:id', async (req, res) => {
    try {
      const channel = await storage.getChannel(req.params.id);
      if (!channel) {
        return res.status(404).json({ message: "Channel not found" });
      }
      res.json(channel);
    } catch (error) {
      console.error("Error fetching channel:", error);
      res.status(500).json({ message: "Failed to fetch channel" });
    }
  });

  app.post('/api/channels', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const channelData = insertChannelSchema.parse({
        ...req.body,
        ownerUserId: userId,
      });
      const channel = await storage.createChannel(channelData);
      res.status(201).json(channel);
    } catch (error) {
      console.error("Error creating channel:", error);
      res.status(500).json({ message: "Failed to create channel" });
    }
  });

  app.patch('/api/channels/:id', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const channel = await storage.getChannel(req.params.id);

      if (!channel) {
        return res.status(404).json({ message: "Channel not found" });
      }

      if (channel.ownerUserId !== userId) {
        return res.status(403).json({ message: "Unauthorized" });
      }

      const updates = insertChannelSchema.partial().parse(req.body);
      const updatedChannel = await storage.updateChannel(req.params.id, updates);
      res.json(updatedChannel);
    } catch (error) {
      console.error("Error updating channel:", error);
      res.status(500).json({ message: "Failed to update channel" });
    }
  });

  app.delete('/api/channels/:id', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const channel = await storage.getChannel(req.params.id);

      if (!channel) {
        return res.status(404).json({ message: "Channel not found" });
      }

      if (channel.ownerUserId !== userId) {
        return res.status(403).json({ message: "Unauthorized" });
      }

      await storage.deleteChannel(req.params.id);
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting channel:", error);
      res.status(500).json({ message: "Failed to delete channel" });
    }
  });

  // Videos routes
  app.get('/api/videos', async (req, res) => {
    try {
      const limit = parseInt(req.query.limit as string) || 20;
      const offset = parseInt(req.query.offset as string) || 0;
      const channelId = req.query.channelId as string;
      const categoryId = req.query.categoryId as string;
      const search = req.query.search as string;

      let videos;
      if (channelId) {
        videos = await storage.getVideosByChannel(channelId, limit, offset);
      } else if (categoryId) {
        videos = await storage.getVideosByCategory(categoryId, limit, offset);
      } else if (search) {
        videos = await storage.searchVideos(search, limit, offset);
      } else {
        videos = await storage.getVideos(limit, offset);
      }

      res.json(videos);
    } catch (error) {
      console.error("Error fetching videos:", error);
      res.status(500).json({ message: "Failed to fetch videos" });
    }
  });

  app.get('/api/videos/:id', async (req, res) => {
    try {
      const video = await storage.getVideo(req.params.id);
      if (!video) {
        return res.status(404).json({ message: "Video not found" });
      }

      // Increment view count
      await storage.incrementViewCount(req.params.id);

      res.json(video);
    } catch (error) {
      console.error("Error fetching video:", error);
      res.status(500).json({ message: "Failed to fetch video" });
    }
  });

  app.post('/api/videos', isAuthenticated, upload.single('video'), async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;

      if (!req.file) {
        return res.status(400).json({ message: "Video file required" });
      }

      const videoData = insertVideoSchema.parse({
        ...req.body,
        videoUrl: req.file.path, // This would be processed and stored properly in production
        tags: req.body.tags ? req.body.tags.split(',').map((tag: string) => tag.trim()) : [],
      });

      // Verify channel ownership
      const channel = await storage.getChannel(videoData.channelId);
      if (!channel || channel.ownerUserId !== userId) {
        return res.status(403).json({ message: "Unauthorized" });
      }

      const video = await storage.createVideo(videoData);
      res.status(201).json(video);
    } catch (error) {
      console.error("Error uploading video:", error);
      res.status(500).json({ message: "Failed to upload video" });
    }
  });

  app.patch('/api/videos/:id', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const video = await storage.getVideo(req.params.id);

      if (!video) {
        return res.status(404).json({ message: "Video not found" });
      }

      // Verify channel ownership
      const channel = await storage.getChannel(video.channelId);
      if (!channel || channel.ownerUserId !== userId) {
        return res.status(403).json({ message: "Unauthorized" });
      }

      const updates = insertVideoSchema.partial().parse(req.body);
      if (updates.tags && typeof updates.tags === 'string') {
        updates.tags = (updates.tags as string).split(',').map((tag: string) => tag.trim());
      }

      const updatedVideo = await storage.updateVideo(req.params.id, updates);
      res.json(updatedVideo);
    } catch (error) {
      console.error("Error updating video:", error);
      res.status(500).json({ message: "Failed to update video" });
    }
  });

  app.delete('/api/videos/:id', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const video = await storage.getVideo(req.params.id);

      if (!video) {
        return res.status(404).json({ message: "Video not found" });
      }

      // Verify channel ownership
      const channel = await storage.getChannel(video.channelId);
      if (!channel || channel.ownerUserId !== userId) {
        return res.status(403).json({ message: "Unauthorized" });
      }

      await storage.deleteVideo(req.params.id);
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting video:", error);
      res.status(500).json({ message: "Failed to delete video" });
    }
  });

  // Subscriptions routes
  app.post('/api/subscriptions', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const { channelId } = req.body;

      if (!channelId) {
        return res.status(400).json({ message: "Channel ID required" });
      }

      const subscription = await storage.subscribe({ userId, channelId });
      res.status(201).json(subscription);
    } catch (error) {
      console.error("Error subscribing:", error);
      res.status(500).json({ message: "Failed to subscribe" });
    }
  });

  app.delete('/api/subscriptions/:channelId', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const channelId = req.params.channelId;

      await storage.unsubscribe(userId, channelId);
      res.status(204).send();
    } catch (error) {
      console.error("Error unsubscribing:", error);
      res.status(500).json({ message: "Failed to unsubscribe" });
    }
  });

  app.get('/api/subscriptions', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const subscriptions = await storage.getUserSubscriptions(userId);
      res.json(subscriptions);
    } catch (error) {
      console.error("Error fetching subscriptions:", error);
      res.status(500).json({ message: "Failed to fetch subscriptions" });
    }
  });

  app.get('/api/subscriptions/:channelId/status', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const channelId = req.params.channelId;
      const isSubscribed = await storage.isSubscribed(userId, channelId);
      res.json({ isSubscribed });
    } catch (error) {
      console.error("Error checking subscription status:", error);
      res.status(500).json({ message: "Failed to check subscription status" });
    }
  });

  // Watch history routes
  app.post('/api/watch-history', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const { videoId, watchTime } = req.body;

      const watchHistory = await storage.addToWatchHistory({
        userId,
        videoId,
        watchTime: watchTime || 0,
      });

      res.json(watchHistory);
    } catch (error) {
      console.error("Error adding to watch history:", error);
      res.status(500).json({ message: "Failed to add to watch history" });
    }
  });

  app.get('/api/watch-history', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const limit = parseInt(req.query.limit as string) || 20;
      const offset = parseInt(req.query.offset as string) || 0;

      const history = await storage.getUserWatchHistory(userId, limit, offset);
      res.json(history);
    } catch (error) {
      console.error("Error fetching watch history:", error);
      res.status(500).json({ message: "Failed to fetch watch history" });
    }
  });

  // Video likes routes
  app.post('/api/videos/:id/like', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const videoId = req.params.id;
      const { isLike } = req.body;

      const like = await storage.likeVideo({ userId, videoId, isLike });
      res.json(like);
    } catch (error) {
      console.error("Error liking video:", error);
      res.status(500).json({ message: "Failed to like video" });
    }
  });

  app.delete('/api/videos/:id/like', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const videoId = req.params.id;

      await storage.removeLike(userId, videoId);
      res.status(204).send();
    } catch (error) {
      console.error("Error removing like:", error);
      res.status(500).json({ message: "Failed to remove like" });
    }
  });

  app.get('/api/videos/:id/like', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const videoId = req.params.id;

      const like = await storage.getUserVideoLike(userId, videoId);
      res.json(like || null);
    } catch (error) {
      console.error("Error fetching video like:", error);
      res.status(500).json({ message: "Failed to fetch video like" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  users,
  channels,
  videos,
  categories,
  subscriptions,
  watchHistory,
  videoLikes,
  type User,
  type UpsertUser,
  type Channel,
  type InsertChannel,
  type Video,
  type InsertVideo,
  type Category,
  type InsertCategory,
  type Subscription,
  type InsertSubscription,
  type WatchHistory,
  type InsertWatchHistory,
  type VideoLike,
  type InsertVideoLike,
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, asc, like, and, or, sql, count } from "drizzle-orm";

export interface IStorage {
  // User operations (IMPORTANT: mandatory for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;

  // Channel operations
  createChannel(channel: InsertChannel): Promise<Channel>;
  getChannel(id: string): Promise<Channel | undefined>;
  getChannelsByOwner(userId: string): Promise<Channel[]>;
  updateChannel(id: string, updates: Partial<InsertChannel>): Promise<Channel | undefined>;
  deleteChannel(id: string): Promise<void>;

  // Video operations
  createVideo(video: InsertVideo): Promise<Video>;
  getVideo(id: string): Promise<Video | undefined>;
  getVideos(limit?: number, offset?: number): Promise<Video[]>;
  getVideosByChannel(channelId: string, limit?: number, offset?: number): Promise<Video[]>;
  getVideosByCategory(categoryId: string, limit?: number, offset?: number): Promise<Video[]>;
  searchVideos(query: string, limit?: number, offset?: number): Promise<Video[]>;
  updateVideo(id: string, updates: Partial<InsertVideo>): Promise<Video | undefined>;
  deleteVideo(id: string): Promise<void>;
  incrementViewCount(videoId: string): Promise<void>;

  // Category operations
  createCategory(category: InsertCategory): Promise<Category>;
  getCategories(): Promise<Category[]>;
  getCategory(id: string): Promise<Category | undefined>;

  // Subscription operations
  subscribe(subscription: InsertSubscription): Promise<Subscription>;
  unsubscribe(userId: string, channelId: string): Promise<void>;
  getUserSubscriptions(userId: string): Promise<Subscription[]>;
  isSubscribed(userId: string, channelId: string): Promise<boolean>;

  // Watch history operations
  addToWatchHistory(watchHistory: InsertWatchHistory): Promise<WatchHistory>;
  getUserWatchHistory(userId: string, limit?: number, offset?: number): Promise<WatchHistory[]>;
  updateWatchTime(userId: string, videoId: string, watchTime: number): Promise<void>;

  // Video likes operations
  likeVideo(videoLike: InsertVideoLike): Promise<VideoLike>;
  removeLike(userId: string, videoId: string): Promise<void>;
  getUserVideoLike(userId: string, videoId: string): Promise<VideoLike | undefined>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  // Channel operations
  async createChannel(channel: InsertChannel): Promise<Channel> {
    const [newChannel] = await db.insert(channels).values(channel).returning();
    return newChannel;
  }

  async getChannel(id: string): Promise<Channel | undefined> {
    const [channel] = await db.select().from(channels).where(eq(channels.id, id));
    return channel;
  }

  async getChannelsByOwner(userId: string): Promise<Channel[]> {
    return await db
      .select()
      .from(channels)
      .where(eq(channels.ownerUserId, userId))
      .orderBy(desc(channels.createdAt));
  }

  async updateChannel(id: string, updates: Partial<InsertChannel>): Promise<Channel | undefined> {
    const [updatedChannel] = await db
      .update(channels)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(channels.id, id))
      .returning();
    return updatedChannel;
  }

  async deleteChannel(id: string): Promise<void> {
    await db.delete(channels).where(eq(channels.id, id));
  }

  // Video operations
  async createVideo(video: InsertVideo): Promise<Video> {
    const [newVideo] = await db.insert(videos).values(video).returning();
    return newVideo;
  }

  async getVideo(id: string): Promise<Video | undefined> {
    const [video] = await db.select().from(videos).where(eq(videos.id, id));
    return video;
  }

  async getVideos(limit = 20, offset = 0): Promise<Video[]> {
    return await db
      .select()
      .from(videos)
      .where(eq(videos.isPublic, true))
      .orderBy(desc(videos.createdAt))
      .limit(limit)
      .offset(offset);
  }

  async getVideosByChannel(channelId: string, limit = 20, offset = 0): Promise<Video[]> {
    return await db
      .select()
      .from(videos)
      .where(and(eq(videos.channelId, channelId), eq(videos.isPublic, true)))
      .orderBy(desc(videos.createdAt))
      .limit(limit)
      .offset(offset);
  }

  async getVideosByCategory(categoryId: string, limit = 20, offset = 0): Promise<Video[]> {
    return await db
      .select()
      .from(videos)
      .where(and(eq(videos.categoryId, categoryId), eq(videos.isPublic, true)))
      .orderBy(desc(videos.createdAt))
      .limit(limit)
      .offset(offset);
  }

  async searchVideos(query: string, limit = 20, offset = 0): Promise<Video[]> {
    return await db
      .select()
      .from(videos)
      .where(
        and(
          eq(videos.isPublic, true),
          or(
            like(videos.title, `%${query}%`),
            like(videos.description, `%${query}%`)
          )
        )
      )
      .orderBy(desc(videos.createdAt))
      .limit(limit)
      .offset(offset);
  }

  async updateVideo(id: string, updates: Partial<InsertVideo>): Promise<Video | undefined> {
    const [updatedVideo] = await db
      .update(videos)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(videos.id, id))
      .returning();
    return updatedVideo;
  }

  async deleteVideo(id: string): Promise<void> {
    await db.delete(videos).where(eq(videos.id, id));
  }

  async incrementViewCount(videoId: string): Promise<void> {
    await db
      .update(videos)
      .set({ viewCount: sql`${videos.viewCount} + 1` })
      .where(eq(videos.id, videoId));
  }

  // Category operations
  async createCategory(category: InsertCategory): Promise<Category> {
    const [newCategory] = await db.insert(categories).values(category).returning();
    return newCategory;
  }

  async getCategories(): Promise<Category[]> {
    return await db.select().from(categories).orderBy(asc(categories.name));
  }

  async getCategory(id: string): Promise<Category | undefined> {
    const [category] = await db.select().from(categories).where(eq(categories.id, id));
    return category;
  }

  // Subscription operations
  async subscribe(subscription: InsertSubscription): Promise<Subscription> {
    const [newSubscription] = await db.insert(subscriptions).values(subscription).returning();

    // Update channel subscriber count
    await db
      .update(channels)
      .set({ subscriberCount: sql`${channels.subscriberCount} + 1` })
      .where(eq(channels.id, subscription.channelId));

    return newSubscription;
  }

  async unsubscribe(userId: string, channelId: string): Promise<void> {
    await db
      .delete(subscriptions)
      .where(and(eq(subscriptions.userId, userId), eq(subscriptions.channelId, channelId)));

    // Update channel subscriber count
    await db
      .update(channels)
      .set({ subscriberCount: sql`GREATEST(0, ${channels.subscriberCount} - 1)` })
      .where(eq(channels.id, channelId));
  }

  async getUserSubscriptions(userId: string): Promise<Subscription[]> {
    return await db
      .select()
      .from(subscriptions)
      .where(eq(subscriptions.userId, userId))
      .orderBy(desc(subscriptions.createdAt));
  }

  async isSubscribed(userId: string, channelId: string): Promise<boolean> {
    const [subscription] = await db
      .select()
      .from(subscriptions)
      .where(and(eq(subscriptions.userId, userId), eq(subscriptions.channelId, channelId)));
    return !!subscription;
  }

  // Watch history operations
  async addToWatchHistory(watchHistoryData: InsertWatchHistory): Promise<WatchHistory> {
    // Check if entry already exists
    const [existing] = await db
      .select()
      .from(watchHistory)
      .where(
        and(
          eq(watchHistory.userId, watchHistoryData.userId),
          eq(watchHistory.videoId, watchHistoryData.videoId)
        )
      );

    if (existing) {
      // Update existing entry
      const [updated] = await db
        .update(watchHistory)
        .set({
          watchTime: watchHistoryData.watchTime || existing.watchTime,
          lastWatchedAt: new Date(),
        })
        .where(eq(watchHistory.id, existing.id))
        .returning();
      return updated;
    } else {
      // Create new entry
      const [newEntry] = await db.insert(watchHistory).values(watchHistoryData).returning();
      return newEntry;
    }
  }

  async getUserWatchHistory(userId: string, limit = 20, offset = 0): Promise<WatchHistory[]> {
    return await db
      .select()
      .from(watchHistory)
      .where(eq(watchHistory.userId, userId))
      .orderBy(desc(watchHistory.lastWatchedAt))
      .limit(limit)
      .offset(offset);
  }

  async updateWatchTime(userId: string, videoId: string, watchTime: number): Promise<void> {
    await db
      .update(watchHistory)
      .set({ watchTime, lastWatchedAt: new Date() })
      .where(
        and(
          eq(watchHistory.userId, userId),
          eq(watchHistory.videoId, videoId)
        )
      );
  }

  // Video likes operations
  async likeVideo(videoLike: InsertVideoLike): Promise<VideoLike> {
    // Check if like already exists
    const [existing] = await db
      .select()
      .from(videoLikes)
      .where(
        and(
          eq(videoLikes.userId, videoLike.userId),
          eq(videoLikes.videoId, videoLike.videoId)
        )
      );

    if (existing) {
      // Update existing like
      const [updated] = await db
        .update(videoLikes)
        .set({ isLike: videoLike.isLike })
        .where(eq(videoLikes.id, existing.id))
        .returning();
      return updated;
    } else {
      // Create new like
      const [newLike] = await db.insert(videoLikes).values(videoLike).returning();
      return newLike;
    }
  }

  async removeLike(userId: string, videoId: string): Promise<void> {
    await db
      .delete(videoLikes)
      .where(
        and(
          eq(videoLikes.userId, userId),
          eq(videoLikes.videoId, videoId)
        )
      );
  }

  async getUserVideoLike(userId: string, videoId: string): Promise<VideoLike | undefined> {
    const [like] = await db
      .select()
      .from(videoLikes)
      .where(
        and(
          eq(videoLikes.userId, userId),
          eq(videoLikes.videoId, videoId)
        )
      );
    return like;
  }
}

export const storage = new DatabaseStorage();

