import { formatDistance } from "date-fns";
import { Badge } from "@/components/ui/badge";
import { Link } from "wouter";

interface VideoCardProps {
  video: {
    id: string;
    title: string;
    thumbnailUrl?: string;
    duration?: number;
    channelId: string;
    viewCount?: number;
    createdAt: string;
    channel?: {
      name: string;
      avatarUrl?: string;
    };
    processingStatus?: string;
  };
}

export default function VideoCard({ video }: VideoCardProps) {
  const formatDuration = (seconds?: number) => {
    if (!seconds) return "00:00";

    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const remainingSeconds = seconds % 60;

    if (hours > 0) {
      return `${hours}:${minutes.toString().padStart(2, '0')}:${remainingSeconds.toString().padStart(2, '0')}`;
    }

    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
  };

  const getChannelInitials = (name: string) => {
    return name.split(' ').map(word => word[0]).join('').toUpperCase().slice(0, 2);
  };

  const getQualityBadge = () => {
    // This would typically come from the processed URLs
    return "1080p";
  };

  return (
    <Link href={`/watch/${video.id}`}>
      <div className="group cursor-pointer" data-testid={`video-card-${video.id}`}>
        <div className="relative aspect-video bg-muted rounded-lg overflow-hidden mb-3">
          {video.thumbnailUrl ? (
            <img 
              src={video.thumbnailUrl}
              alt={video.title}
              className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-300"
            />
          ) : (
            <div className="w-full h-full bg-muted flex items-center justify-center">
              <div className="text-muted-foreground">No thumbnail</div>
            </div>
          )}

          {video.duration && (
            <div className="absolute bottom-2 right-2 bg-black/80 text-white text-xs px-2 py-1 rounded" data-testid="text-duration">
              {formatDuration(video.duration)}
            </div>
          )}

          <div className="absolute top-2 left-2">
            <Badge variant="secondary" className="bg-accent text-accent-foreground text-xs" data-testid="text-quality">
              {getQualityBadge()}
            </Badge>
          </div>

          {video.processingStatus === "processing" && (
            <div className="absolute inset-0 bg-black/50 flex items-center justify-center">
              <Badge variant="outline" className="bg-background/80">Processing...</Badge>
            </div>
          )}
        </div>

        <div className="flex gap-3">
          <div className="w-8 h-8 bg-primary rounded-full flex-shrink-0 flex items-center justify-center">
            {video.channel?.avatarUrl ? (
              <img 
                src={video.channel.avatarUrl}
                alt={`${video.channel.name} avatar`}
                className="w-full h-full object-cover rounded-full"
              />
            ) : (
              <span className="text-primary-foreground text-xs font-semibold">
                {video.channel?.name ? getChannelInitials(video.channel.name) : 'CH'}
              </span>
            )}
          </div>

          <div className="flex-1 min-w-0">
            <h4 className="font-medium text-foreground mb-1 line-clamp-2 group-hover:text-primary transition-colors" data-testid="text-video-title">
              {video.title}
            </h4>
            {video.channel && (
              <p className="text-sm text-muted-foreground mb-1" data-testid="text-channel-name">
                {video.channel.name}
              </p>
            )}
            <p className="text-sm text-muted-foreground" data-testid="text-video-stats">
              {video.viewCount?.toLocaleString() || 0} views â€¢ {formatDistance(new Date(video.createdAt), new Date(), { addSuffix: true })}
            </p>
          </div>
        </div>
      </div>
    </Link>
  );
}

client/src/components/video-grid.tsx.content:
import { useQuery } from "@tanstack/react-query";
import VideoCard from "@/components/video-card";
import { Skeleton } from "@/components/ui/skeleton";

interface VideoGridProps {
  channelId?: string;
  categoryId?: string;
  searchQuery?: string;
  limit?: number;
}

export default function VideoGrid({ channelId, categoryId, searchQuery, limit = 20 }: VideoGridProps) {
  const { data: videos, isLoading, error } = useQuery({
    queryKey: ["/api/videos", { channelId, categoryId, searchQuery, limit }],
    queryFn: () => {
      const params = new URLSearchParams();
      if (channelId) params.append('channelId', channelId);
      if (categoryId) params.append('categoryId', categoryId);
      if (searchQuery) params.append('search', searchQuery);
      params.append('limit', limit.toString());

      return fetch(`/api/videos?${params}`).then(res => res.json());
    },
  });

  if (isLoading) {
    return (
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
        {Array.from({ length: 8 }).map((_, i) => (
          <div key={i} className="space-y-3">
            <Skeleton className="aspect-video w-full rounded-lg" />
            <div className="flex gap-3">
              <Skeleton className="w-8 h-8 rounded-full flex-shrink-0" />
              <div className="flex-1 space-y-2">
                <Skeleton className="h-4 w-full" />
                <Skeleton className="h-3 w-2/3" />
                <Skeleton className="h-3 w-1/2" />
              </div>
            </div>
          </div>
        ))}
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center py-8">
        <p className="text-muted-foreground">Failed to load videos. Please try again later.</p>
      </div>
    );
  }

  if (!videos || videos.length === 0) {
    return (
      <div className="text-center py-8">
        <p className="text-muted-foreground" data-testid="text-no-videos">
          {searchQuery ? "No videos found for your search." : "No videos available."}
        </p>
      </div>
    );
  }

  return (
    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6" data-testid="video-grid">
      {videos.map((video: any) => (
        <VideoCard key={video.id} video={video} />
      ))}
    </div>
  );
}

client/src/components/video-player.tsx.content:
import { useRef, useEffect, useState } from "react";
import { Button } from "@/components/ui/button";
import { Slider } from "@/components/ui/slider";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Play, Pause, Volume2, VolumeX, Maximize, Settings } from "lucide-react";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";

interface VideoPlayerProps {
  videoId: string;
  videoUrl: string;
  title: string;
}

export default function VideoPlayer({ videoId, videoUrl, title }: VideoPlayerProps) {
  const videoRef = useRef<HTMLVideoElement>(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const [volume, setVolume] = useState(100);
  const [isMuted, setIsMuted] = useState(false);
  const [quality, setQuality] = useState("auto");
  const [showControls, setShowControls] = useState(true);
  const controlsTimeoutRef = useRef<NodeJS.Timeout>();

  const trackWatchTime = useMutation({
    mutationFn: async (watchTime: number) => {
      await apiRequest("POST", "/api/watch-history", {
        videoId,
        watchTime: Math.floor(watchTime),
      });
    },
  });

  useEffect(() => {
    const video = videoRef.current;
    if (!video) return;

    const handleTimeUpdate = () => {
      setCurrentTime(video.currentTime);

      // Track watch time every 10 seconds
      if (Math.floor(video.currentTime) % 10 === 0) {
        trackWatchTime.mutate(video.currentTime);
      }
    };

    const handleDurationChange = () => {
      setDuration(video.duration);
    };

    const handlePlay = () => setIsPlaying(true);
    const handlePause = () => setIsPlaying(false);

    video.addEventListener("timeupdate", handleTimeUpdate);
    video.addEventListener("durationchange", handleDurationChange);
    video.addEventListener("play", handlePlay);
    video.addEventListener("pause", handlePause);

    return () => {
      video.removeEventListener("timeupdate", handleTimeUpdate);
      video.removeEventListener("durationchange", handleDurationChange);
      video.removeEventListener("play", handlePlay);
      video.removeEventListener("pause", handlePause);
    };
  }, [videoId, trackWatchTime]);

  const togglePlay = async () => {
    const video = videoRef.current;
    if (!video) return;

    try {
      if (isPlaying) {
        video.pause();
      } else {
        // Only try to play if video has a valid source
        if (video.src || videoUrl) {
          await video.play();
        } else {
          console.warn('No video source available for playback');
        }
      }
    } catch (error) {
      console.warn('Video playback failed:', error);
      // Reset playing state if play failed
      setIsPlaying(false);
    }
  };

  const handleSeek = (value: number[]) => {
    const video = videoRef.current;
    if (!video) return;

    video.currentTime = value[0];
    setCurrentTime(value[0]);
  };

  const handleVolumeChange = (value: number[]) => {
    const video = videoRef.current;
    if (!video) return;

    const newVolume = value[0];
    setVolume(newVolume);
    video.volume = newVolume / 100;

    if (newVolume === 0) {
      setIsMuted(true);
    } else {
      setIsMuted(false);
    }
  };

  const toggleMute = () => {
    const video = videoRef.current;
    if (!video) return;

    if (isMuted) {
      video.volume = volume / 100;
      setIsMuted(false);
    } else {
      video.volume = 0;
      setIsMuted(true);
    }
  };

  const toggleFullscreen = () => {
    const video = videoRef.current;
    if (!video) return;

    if (document.fullscreenElement) {
      document.exitFullscreen();
    } else {
      video.requestFullscreen();
    }
  };

  const formatTime = (time: number) => {
    const hours = Math.floor(time / 3600);
    const minutes = Math.floor((time % 3600) / 60);
    const seconds = Math.floor(time % 60);

    if (hours > 0) {
      return `${hours}:${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`;
    }

    return `${minutes}:${seconds.toString().padStart(2, '0')}`;
  };

  const handleMouseMove = () => {
    setShowControls(true);

    if (controlsTimeoutRef.current) {
      clearTimeout(controlsTimeoutRef.current);
    }

    controlsTimeoutRef.current = setTimeout(() => {
      if (isPlaying) {
        setShowControls(false);
      }
    }, 3000);
  };

  const handleMouseLeave = () => {
    if (controlsTimeoutRef.current) {
      clearTimeout(controlsTimeoutRef.current);
    }

    // Hide controls after mouse leaves, regardless of playing state
    controlsTimeoutRef.current = setTimeout(() => {
      setShowControls(false);
    }, 1000);
  };

  return (
    <div 
      className="relative w-full bg-black rounded-lg overflow-hidden group"
      onMouseMove={handleMouseMove}
      onMouseLeave={handleMouseLeave}
    >
      <video
        ref={videoRef}
        className="w-full aspect-video object-contain"
        onClick={togglePlay}
        data-testid="video-element"
        preload="metadata"
      >
        {videoUrl && <source src={videoUrl} type="video/mp4" />}
        <div className="w-full h-full bg-muted flex items-center justify-center">
          <div className="text-muted-foreground text-center">
            <div className="text-4xl mb-2">ðŸ“¹</div>
            <div>Video Player Ready</div>
            {!videoUrl && <div className="text-sm mt-1">No video source provided</div>}
          </div>
        </div>
      </video>

      {/* Video Controls Overlay */}
      <div 
        className={`absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black/80 via-black/40 to-transparent p-4 transition-opacity duration-300 ${
          showControls ? 'opacity-100' : 'opacity-0'
        }`}
      >
        {/* Progress Bar */}
        <div className="mb-4">
          <Slider
            value={[currentTime]}
            max={duration}
            step={0.1}
            onValueChange={handleSeek}
            className="w-full"
            data-testid="video-progress-slider"
          />
        </div>

        {/* Control Buttons */}
        <div className="flex items-center justify-between text-white">
          <div className="flex items-center gap-4">
            <Button
              variant="ghost"
              size="icon"
              onClick={togglePlay}
              className="text-white hover:bg-white/20"
              data-testid="button-play-pause"
            >
              {isPlaying ? <Pause className="w-5 h-5" /> : <Play className="w-5 h-5" />}
            </Button>

            <div className="flex items-center gap-2 text-sm">
              <span data-testid="text-current-time">{formatTime(currentTime)}</span>
              <span>/</span>
              <span data-testid="text-total-time">{formatTime(duration)}</span>
            </div>
          </div>

          <div className="flex items-center gap-4">
            <div className="flex items-center gap-2">
              <Button
                variant="ghost"
                size="icon"
                onClick={toggleMute}
                className="text-white hover:bg-white/20"
                data-testid="button-mute"
              >
                {isMuted ? <VolumeX className="w-4 h-4" /> : <Volume2 className="w-4 h-4" />}
              </Button>
              <div className="w-16">
                <Slider
